const monthlyPayments=(a,e,t)=>{let n=1*e/12/100,l=a*(n*Math.pow(1+n,t))/(Math.pow(1+n,t)-1);return Math.ceil(l)};$(document).ready(function(){$("input.form-control").on("keyup",a=>{let e=parseInt($("#cost")[0].value)-parseInt($("#first-payment")[0].value),t=parseInt($("#period")[0].value),n=parseFloat($("#percent")[0].value),l=monthlyPayments(e,n,t);$("#amount")[0].value=e,$("#amount-label").addClass("active"),$("#payment")[0].value=l||"Недостаточно данных",$("#payment-label").addClass("active")})});