const monthlyPayments=(t,e,a)=>{let n=1*e/12/100,o=t*(n*Math.pow(1+n,a))/(Math.pow(1+n,a)-1);return Math.ceil(o)};$(document).ready(function(){$("input.form-control").on("keyup",t=>{let e=parseInt($("#cost")[0].value)-parseInt($("#first-payment")[0].value),a=parseInt($("#period")[0].value),n=parseFloat($("#percent")[0].value),o=monthlyPayments(e,n,a);$("#amount")[0].value=e,$("#payment")[0].value=o||"Недостаточно данных"})});